mapper.select:
SELECT 	menu_idx, menu_name, menu_price, menu_category, menu_picture, option1_YN, option2_YN, option3_YN
FROM manage_menu
WHERE menu_idx = #{menu_idx}

mapper.update:
UPDATE 	order_number
SET		active_YN= 'C'
WHERE 	order_no = #{order_no};

UPDATE	payment
SET		pay_status = 'C'
WHERE	order_no = #{order_no};
